mapper.select:
SELECT t.`id`,t.`initials`, IF(m.id IS NULL AND e.id IS NULL,t.`name`,IF(m.`id` IS NULL AND e.`id` IS NOT NULL ,e.`name`,m.name)) AS 'province', IF(m.id IS NULL AND e.id IS NULL,e.`name`,IF(m.`id` IS NULL AND e.`id` IS NOT NULL ,t.`name`,e.name)) AS 'city', IF(m.id IS NULL AND e.id IS NULL,m.`name`,IF(m.`id` IS NULL AND e.`id` IS NOT NULL ,m.`name`,t.name)) AS 'county' FROM tb_dic_area_info t LEFT JOIN tb_dic_area_info e ON t.`parentID` = e.`id` LEFT JOIN tb_dic_area_info m ON e.`parentID` = m.`id` where 1=1
AND (t.initials LIKE '%${query}%' OR t.name LIKE '%${query}%')
AND t.id = #{area_code}

SELECT pm.`code` AS 'key', pm.`text` AS 'value' FROM tb_dic_print_model pm LEFT JOIN tb_partner_model_config c ON c.`code` = pm.`code` WHERE 1=1
AND c.`userid`= #{userid}
AND pm.type=#{type}
GROUP BY pm.code

SELECT ty.typeid AS id,ty.typename AS TEXT,ty.displaygrid
AS url FROM tb_upc_user_report rp,tb_judicial_report_type ty WHERE
rp.typeid=ty.typeid and rp.usercode=#{usercode}

SELECT a.username, d.areaname,a.initials, a.area_id AS id,IF( a.`roletype` = 104, (SELECT u3.username FROM tb_judicial_agent ag, tb_upc_users u3 WHERE ag.userid = a.userid AND u3.userid = ag.peruserid AND ag.delstatus=0 and u3.delstatus is null), '' ) AS agent FROM (SELECT a.*,u.username,u.initials,u.roletype FROM tb_upc_area a, tb_upc_users u, tb_upc_department de WHERE u.`deptcode` = de.`deptcode` AND u.`userid` = a.`userid` AND a.`is_delete` = 0
AND a.`area_id`=#{area_id}
and u.initials LIKE '%${query}%'
and de.companyid=#{companyid}
)a LEFT JOIN (SELECT i.areaname,i.`areacode`,d.initials,d.name FROM tb_upc_area_info i, tb_dic_area_info d WHERE i.code = d.id ) d ON d.`areacode` = a.`areacode` ORDER BY d.areaname

select t.*,k.keyname from tb_upc_dic_values t,tb_upc_dic_keys k WHERE 1=1 AND t.keycode=k.keycode
and (t.keyvalue like '%${search}%' or t.keycode = #{search})
order by t.keycode asc limit #{start},#{end}

SELECT CONCAT(u.username,"(",u.`usercode`,")") AS VALUE,u.userid AS 'key' FROM tb_upc_users u LEFT JOIN tb_upc_department t ON u.deptcode = t.deptcode LEFT JOIN tb_upc_company c ON t.companyid = c.companyid LEFT JOIN tb_upc_role r ON u.roletype = r.roletype WHERE u.usercode!='admin' AND u.delstatus IS NULL AND t.delstatus = 0
AND u.initials like '%${query}%'
AND u.userid = #{userid}
