mapper.select:
SELECT SUM(t.`return_sum`) AS program_real_sum ,COUNT(t.`case_id`) AS program_case_num,SUM(t.sample_count) AS sample_count, SUM(t.`special_count1`) AS special_count1,SUM(t.`special_count2`) AS special_count2 FROM `tb_all_case_finance_info` t
and t.`confirm_date` LIKE '%${confirm_date}%'
and (t.`case_subtype` LIKE '%${case_subtype}%' or t.case_type like '%${case_subtype}%')
and t.`partner` = #{partner}
and t.`user_dept_level1` = #{user_dept_level1}

select * from tb_finance_config_aptitude t 


		and t.`provice` LIKE '%${provice}%'


		and t.`partnername` LIKE '%${partnername}%'


		and t.`partner_flag` = #{partner_flag}



	limit #{start},#{limit}

SELECT 
  	SUM(t.`return_sum`) AS deptServiceSum
	FROM tb_finance_case_detail t 
	WHERE DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d') >= #{confirm_date_start}
	AND #{confirm_date_end} >= DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d') 
	AND (t.type = '服务收入' or t.type = '合作方收入' or t.type = '代理收入')

 	 	AND t.`user_dept_level1` = #{deptname} 


 	 	AND t.`user_dept_level2` = #{deptname1} 


 	 	AND t.`user_dept_level3` = #{deptname2} 


 	 	AND t.`user_dept_level4` = #{deptname3} 


 	 	AND t.`user_dept_level5` = #{deptname4}

SELECT 
  	SUM(t.`return_sum`) AS deptServiceSum,
 	 t.`user_dept_level2` 
	FROM
  tb_finance_case_detail t 
	WHERE DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d') >= #{confirm_date_start}
	AND #{confirm_date_end} >= DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d') 
	AND (t.type = '服务收入' or t.type = '合作方收入' or t.type = '代理收入')
	AND t.`user_dept_level1` = #{deptname} 
  AND t.`user_dept_level2` = #{deptname1}

SELECT 
  	SUM(t.`wages`) AS wagesSum
	FROM tb_finance_wages t 
	WHERE t.`wages_month` >= #{confirm_date_start}
	AND #{confirm_date_end} >= t.`wages_month` 

 	 	AND t.`user_dept_level1` = #{deptname} 


 	 	AND t.`user_dept_level2` = #{deptname1} 


 	 	AND t.`user_dept_level3` = #{deptname2} 


 	 	AND t.`user_dept_level4` = #{deptname3} 


 	 	AND t.`user_dept_level5` = #{deptname4}

SELECT 
  	SUM(t.`wages`) AS wagesSum,
 	 t.`user_dept_level2` 
	FROM
  tb_finance_wages t 
	WHERE t.`wages_month` >= #{confirm_date_start}
	AND #{confirm_date_end} >= t.`wages_month` 
	AND t.`user_dept_level1` = #{deptname} 
  AND t.`user_dept_level2` = #{deptname1}

SELECT 
  	SUM(t.`return_sum`) AS deptSellSum
	FROM tb_finance_case_detail t 
	WHERE t.`confirm_date` >= #{confirm_date_start}
	AND #{confirm_date_end} >= t.`confirm_date` 
	AND t.type='一次性收入'

 	 	AND t.`user_dept_level1` = #{deptname} 


 	 	AND t.`user_dept_level2` = #{deptname1} 


 	 	AND t.`user_dept_level3` = #{deptname2} 


 	 	AND t.`user_dept_level4` = #{deptname3} 


 	 	AND t.`user_dept_level5` = #{deptname4}

SELECT 
  	SUM(t.`return_sum`) AS deptSellSum,
 	 t.`user_dept_level2` 
	FROM
  tb_finance_case_detail t 
	WHERE t.`confirm_date` >= #{confirm_date_start}
	AND #{confirm_date_end} >= t.`confirm_date` 
	AND t.type='一次性收入'
	AND t.`user_dept_level1` = #{deptname} 
  AND t.`user_dept_level2` = #{deptname1}


	  SELECT SUM(t.`mx1bxje`) AS deptExternalCostSum,
		t.`ztsybmc` as user_dept_level1
	 FROM `tb_finance_oa1` t 
	 WHERE t.kmmc = #{amoeba_program}
	 AND #{confirm_date_end} >= t.`operatedate` 
	 AND t.`operatedate` >= #{confirm_date_start}

 	 	AND t.`ztsybmc` = #{deptname} 


 	 	AND t.`ztsybmc` = #{deptname1} 


 	 	AND t.`ztsybmc` = #{deptname2}

SELECT SUM(a.deptSaleManageSum) AS deptSaleManageSum,a.user_dept_level1 FROM (
	SELECT SUM(t.`mx1bxje`) AS deptSaleManageSum,
				t.`ztsybmc` AS user_dept_level1
			 FROM `tb_finance_oa1` t 
			 WHERE t.kmmc NOT IN ('耗材','委外检测成本','仪器设备','采购仪器设备付款')
			 AND #{confirm_date_end} >= t.`operatedate` 
			 AND t.`operatedate` >= #{confirm_date_start}

		 	 	AND t.`ztsybmc` = #{deptname} 


		 	 	AND t.`ztsybmc` = #{deptname1} 


		 	 	AND t.`ztsybmc` = #{deptname2} 

	UNION ALL	 
	SELECT SUM(t.`jkje`) AS deptSaleManageSum,t.`sqgsmc` AS user_dept_level1 FROM tb_finance_oa2 t 
	WHERE #{confirm_date_end} >= t.`operatedate` 
 	AND t.`operatedate` >= #{confirm_date_start} 

 	 	AND t.`sqgsmc` = #{deptname} 


 	 	AND t.`sqgsmc` = #{deptname1} 


 	 	AND t.`sqgsmc` = #{deptname2} 
	 )a

SELECT 
  	SUM(t.`aptitudeCost`) AS deptAptitudeCostSum,
 	 t.`insideCostUnit` 
	FROM
  tb_finance_case_detail t 
	WHERE DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d') >= #{confirm_date_start}
	AND #{confirm_date_end} >= DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d') 

 	 	AND t.`insideCostUnit` = #{deptname} 


 	 	AND t.`insideCostUnit` = #{deptname1} 


 	 	AND t.`insideCostUnit` = #{deptname2}

SELECT 
  	SUM(t.`aptitudeCost`) AS deptAptitudeCostSum,
 	 t.`insideCostUnit` 
	FROM
  tb_finance_case_detail t 
	WHERE DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d') >= #{confirm_date_start}
	AND #{confirm_date_end} >= DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d') 
	AND t.`insideCostUnit` = #{deptname} 
  AND t.`insideCostUnit` = #{deptname1}


	SELECT SUM(t.`mx1bxje`) AS materialSum,
		t.`ztsybmc` as amoeba_deptment,
		t.kmmc as amoeba_program
	 FROM `tb_finance_oa1` t 
	 WHERE t.kmmc = #{amoeba_program}
	 AND #{confirm_date_end} >= t.`operatedate` 
	 AND t.`operatedate` >= #{confirm_date_start}

 	 	AND t.`ztsybmc` = #{deptname} 


 	 	AND t.`ztsybmc` = #{deptname1} 


 	 	AND t.`ztsybmc` = #{deptname2}

	SELECT SUM(t.`mx1bxje`) AS instrumentSum,
		t.`ztsybmc` as amoeba_deptment,
		t.kmmc as amoeba_program
	 FROM `tb_finance_oa1` t 
	 WHERE t.kmmc IN ('仪器设备','采购仪器设备付款')
	 AND #{confirm_date_end} >= t.`operatedate` 
	 AND t.`operatedate` >= #{confirm_date_start}

 	 	AND t.`ztsybmc` = #{deptname} 


 	 	AND t.`ztsybmc` = #{deptname1} 


 	 	AND t.`ztsybmc` = #{deptname2}

SELECT 
  SUM(t.`amoeba_sum`) AS depreciationSum,
  t.`amoeba_program`,
  t.`amoeba_deptment` 
FROM
  `tb_finance_amoeba_info` t 
WHERE t.`amoeba_month` >= #{confirm_date_start}
	AND #{confirm_date_end} >= t.`amoeba_month` 
    AND t.`amoeba_program` = #{amoeba_program}

 	 	AND t.`amoeba_deptment` = #{deptname} 


 	 	AND t.`amoeba_deptment` = #{deptname1} 


 	 	AND t.`amoeba_deptment` = #{deptname2}


	  SELECT SUM(t.`mx1bxje`) AS deptExternalCostSum,
		t.`ssbmmc` as user_dept_level2
	 FROM `tb_finance_oa1` t 
	 WHERE t.kmmc = #{amoeba_program}
	 AND #{confirm_date_end} >= t.`operatedate` 
	 AND t.`operatedate` >= #{confirm_date_start}

 	 	AND t.`ztsybmc` = #{deptname} 


 	 	AND t.`ztsybmc` = #{deptname1} 


 	 	AND t.`ztsybmc` = #{deptname2}

 SELECT SUM(t.`manageCost`)+SUM(t.`insideCost`) AS costInsideSum,t.user_dept_level1 
  FROM tb_finance_case_detail t
  WHERE DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d')  >= #{confirm_date_start}
	AND #{confirm_date_end} >= DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d')  

 	 	AND t.`user_dept_level1` = #{deptname} 


 	 	AND t.`user_dept_level2` = #{deptname1} 


 	 	AND t.`user_dept_level3` = #{deptname2} 


 	 	AND t.`user_dept_level4` = #{deptname3} 


 	 	AND t.`user_dept_level5` = #{deptname4}

 SELECT SUM(t.`manageCost`)+SUM(t.`insideCost`) AS costInsideSum,t.user_dept_level2
  FROM tb_finance_case_detail t
  WHERE DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d')  >= #{confirm_date_start}
	AND #{confirm_date_end} >= DATE_FORMAT(t.`confirm_date`, '%Y-%m-%d')  
	AND t.`user_dept_level1` = #{deptname} 
  AND t.`user_dept_level2` = #{deptname1}

SELECT IF( ISNULL(t4.`deptname`) && ! ISNULL(t3.`deptname`), t3.`deptname`, IF( ISNULL(t3.`deptname`) && ! t2.`deptname`, t2.`deptname`, IF( ISNULL(t2.`deptname`) && ! ISNULL(t1.`deptname`), t1.`deptname`, IF( ISNULL(t1.`deptname`) && ! ISNULL(t.`deptname`), t.`deptname`, t4.`deptname` ) ) ) ) AS 'user_dept_level1', IF( ISNULL(t4.`deptcode`) && ! ISNULL(t3.`deptcode`), t3.`deptcode`, IF( ISNULL(t3.`deptcode`) && ! t2.`deptcode`, t2.`deptcode`, IF( ISNULL(t2.`deptcode`) && ! ISNULL(t1.`deptcode`), t1.`deptcode`, IF( ISNULL(t1.`deptcode`) && ! ISNULL(t.`deptcode`), t.`deptcode`, t4.`deptcode` ) ) ) ) AS 'deptcode1', IF( ! ISNULL(t4.`deptname`), t3.`deptname`, IF( ! ISNULL(t3.`deptname`), t2.`deptname`, IF( ! ISNULL(t2.`deptname`), t1.`deptname`, IF( ! ISNULL(t1.`deptname`), t.`deptname`, t4.`deptname` ) ) ) ) AS 'user_dept_level2', IF( ! ISNULL(t4.`deptcode`), t3.`deptcode`, IF( ! ISNULL(t3.`deptcode`), t2.`deptcode`, IF( ! ISNULL(t2.`deptcode`), t1.`deptcode`, IF( ! ISNULL(t1.`deptcode`), t.`deptcode`, t4.`deptcode` ) ) ) ) AS 'deptcode2', IF( ! ISNULL(t4.`deptname`), t2.`deptname`, IF( ! ISNULL(t3.`deptname`), t1.`deptname`, IF( ! ISNULL(t2.`deptname`), t.`deptname`, NULL ) ) ) AS 'user_dept_level3', IF( ! ISNULL(t4.`deptcode`), t2.`deptcode`, IF( ! ISNULL(t3.`deptcode`), t1.`deptcode`, IF( ! ISNULL(t2.`deptcode`), t.`deptcode`, NULL ) ) ) AS 'deptcode3', IF( ! ISNULL(t4.`deptname`), t1.`deptname`, IF( ! ISNULL(t3.`deptname`), t.`deptname`, NULL ) ) AS 'user_dept_level4', IF( ! ISNULL(t4.`deptcode`), t1.`deptcode`, IF( ! ISNULL(t3.`deptcode`), t.`deptcode`, NULL ) ) AS 'deptcode4', IF( ! ISNULL(t4.`deptname`), t.`deptname`, NULL ) AS 'user_dept_level5', IF( ! ISNULL(t4.`deptcode`), t.`deptcode`, NULL ) AS 'deptcode5', t.deptcode FROM tb_upc_department t LEFT JOIN tb_upc_department t1 ON t1.`deptcode` = t.`parentdeptcode` LEFT JOIN tb_upc_department t2 ON t2.`deptcode` = t1.`parentdeptcode` LEFT JOIN tb_upc_department t3 ON t3.`deptcode` = t2.`parentdeptcode` LEFT JOIN tb_upc_department t4 ON t4.`deptcode` = t3.`parentdeptcode` WHERE 1=1
and t.`deptcode`=#{deptcode}
and t.`deptid`=#{deptid}
