mapper.update:
update tb_upc_users
set
deptcode = #{deptcode},
deptid = #{deptid}
where usercode = #{usercode}

update tb_upc_users
set password=#{password}
where userid in (${userid})

update tb_upc_users
set roletype=#{roletype}
where usercode in (${usercode})

mapper.select:
SELECT u.`userid`,u.`username`,d.`companyid`,u.telphone FROM tb_upc_users
u,`tb_upc_department` d WHERE u.`deptcode` = d.`deptcode` AND
u.`delstatus` IS NULL AND d.`delstatus`=0

	AND d.`companyid` = #{companyid}


	AND u.`initials` LIKE '%${query}%'


	AND u.`userid` = #{userid}

SELECT u.userid, u.usercode, u.username, u.usertype, u.sex, u.email, u.address, u.usertype as typename, u.telphone, u.qq, u.webchart, u.deptcode, u.companyid, c.companyname, r.rolename, u.certificateno, u.relation_id , u.areacode, u.parnter_name, u.receiver_area, t.deptcode, t.deptname FROM tb_upc_users u LEFT JOIN `tb_upc_department` t ON u.deptcode = t.deptcode LEFT JOIN tb_upc_company c ON u.companyid = c.companyid LEFT JOIN tb_upc_role r ON u.roletype = r.roletype WHERE 1 = 1 AND u.delstatus IS NULL
and u.companyid = #{companyid}
and (u.username like concat('%', #{username}, '%') or u.usercode like concat('%', #{username}, '%'))
and u.usercode = #{usercode}
and r.roletype = #{roletype}
and u.usertype = #{usertype}
and u.deptcode = #{deptcode}
and u.receiver_area like concat('%', #{receiver_area}, '%')
and u.parnter_name like concat('%', #{parnter_name}, '%')
and t.deptname LIKE concat('%', #{dept_name}, '%')
LIMIT #{start},#{end}

SELECT COUNT(*) FROM tb_upc_users u
LEFT JOIN tb_upc_department t ON u.deptcode=t.deptcode
LEFT JOIN tb_upc_company c ON u.companyid=c.companyid
LEFT JOIN tb_upc_dict d ON u.usertype=d.typecode
LEFT JOIN tb_upc_role r ON u.roletype=r.roletype
WHERE 1=1 AND u.delstatus IS NULL

	and u.companyid = #{companyid}


	and u.username like concat('%', #{username}, '%')


	and u.usercode = #{usercode}


	and u.userid = #{userid}


	and r.roletype = #{roletype}


	and u.usertype = #{usertype}


	and u.deptcode = #{deptcode}


	and u.receiver_area like concat('%', #{receiver_area}, '%')


	and u.parnter_name like concat('%', #{parnter_name}, '%')


	and t.deptname LIKE  concat('%', #{dept_name}, '%')

SELECT t.*,d.`companyid`,d.`deptid` FROM tb_upc_users t LEFT JOIN tb_upc_department d ON t.`deptcode`=d.`deptcode` WHERE 1=1 AND t.delstatus IS NULL

	and t.usercode=#{usercode}


	and t.userid=#{userid}
